<template>
  <div>
    <div id="box" style="margin-top: 20px;">   
      <div class="show">
        <img :src="headerImage" style="width:100%;">
        <div class="picture" :style="'backgroundImage:url('+headerImage+')'"></div>
      </div>
      <p style="font-size:15px;font-weight:bold;text-align:center;margin-top:20px">请自拍大头照、平视屏幕，并正对光源</p> 
    </div>
    <div style="padding:10px;" >
        <input ref="fileinput" v-show="false" type="file" id="upload" accept="image/*" capture="camera" @change="upload" />
        <label for="upload" id="poxreal" style="background-color:#ffaa00;border-color:transparent;margin-top:10px"> 
          开始拍摄
          <!-- <button class="am-button" >开始拍摄</button> -->
        </label>
    </div>
  </div>
</template>

<script>
export default {
  components: {},
  data() {
    return {
      picValue: {},
      headerImageurl: '',
      headerImage: 'data:image/jpeg;base64,',
    };
  },
  methods: {
    upload(e) {
      const files = e.target.files || e.dataTransfer.files;
      if (!files.length) return;
      this.picValue = files[0]; // eslint-disable-line
      this.imgPreview(this.picValue);
    },
    imgPreview(file) {
      const self = this;
      // 看支持不支持FileReader
      if (!file || !window.FileReader) return;
      if (/^image/.test(file.type)) {
        // 创建一个reader
        const reader = new FileReader();
        // 将图片2将转成 base64 格式
        reader.readAsDataURL(file);
        // 读取成功后的回调
        reader.onloadend = function () { // eslint-disable-line
          // console.log('lal', self.picValue.size / 1024);
          // 判断图片是否大于100K,是就直接上传，反之压缩图片
          if (self.picValue.size <= (100 * 1024)) {
            self.headerImage = this.result;
            self.headerImageurl = self.headerImage.substring(self.headerImage.indexOf(',') + 1);
            // self.postImg();
          } else {
            const imgObj = new Image();
            imgObj.src = this.result;
            // eslint-disable-next-line
            imgObj.onload = function() {
              const ratio = Number(Math.sqrt(this.width * this.height / 10000 / 48).toFixed(2)); // 压缩比例 ，我这是压倒48万像素的需求
              let img64 = self.compress(imgObj, this.width / ratio, this.height / ratio, 0.9);
              if (Number(self.showSize(img64)) >= 150) {
                const effect = 1 / (self.showSize(img64) / 130);
                img64 = self.compress(imgObj, this.width / ratio, this.height / ratio, effect);
              }
              self.headerImage = img64;
              self.headerImage = img64;
            };
          }
        };
      }
    },
    postImg() {
      //此处调用服务端接口
    },
    compress(img, width, height, ratio) {
      const canvas = document.createElement('canvas');
      canvas.width = width;
      canvas.height = height;
      const ctx = canvas.getContext('2d');
      ctx.drawImage(img, 0, 0, width, height);
      const img64 = canvas.toDataURL('image/jpeg', ratio);
      return img64;
    },
    showSize(base64url) {
      let str = base64url.replace('data:image/jpeg;base64,', '');
      const equalIndex = str.indexOf('=');
      if (str.indexOf('=') > 0) {
        str = str.substring(0, equalIndex);
      }
      const strLength = str.length;
      const fileLength = parseInt(strLength - (strLength / 8) * 2);
      // 由字节转换为KB
      let size = '';
      size = (fileLength / 1024).toFixed(2);
      const sizeStr = `${size} `; // 转成字符串
      const index = sizeStr.indexOf('.'); // 获取小数点处的索引
      const dou = sizeStr.substr(index + 1, 2); // 获取小数点后两位的值
      if (dou === '00') {
        return sizeStr.substring(0, index) + sizeStr.substr(index + 3, 2);
      }
      return parseInt(size);
    },
  },
};
</script>

